args:
.lottery-winner {
    font-size: 32px !important;
    color: #ffcc00 !important;
    border-bottom: 4px solid #ffcc00 !important;
    text-shadow: 0 0 10px #ffcc00, 0 0 30px #ff0000 !important;
    z-index: 1000;
    animation: winnerPulse 1s infinite alternate;
}

@keyframes winnerPulse {
    from { transform: scale(1); }
    to { transform: scale(1.2); }
}

#gesture-toggle-btn {
    position: absolute;
    top: 25px;
    right: 220px; /* Left of BGM button (80 + 120 + 20) */
    color: rgba(0, 255, 255, 0.7);
    font-family: 'Share Tech Mono', monospace;
    font-size: 14px;
    letter-spacing: 2px;
    border: 1px solid rgba(0, 255, 255, 0.3);
    padding: 8px 16px;
    cursor: pointer;
    background: linear-gradient(-90deg, transparent 0%, rgba(0, 255, 255, 0.05) 100%);
    clip-path: polygon(0 0, calc(100% - 10px) 0, 100% 10px, 100% 100%, 10px 100%, 0 calc(100% - 10px));
    transition: all 0.3s ease;
    z-index: 10003;
    user-select: none;
    pointer-events: auto;
    overflow: hidden;
    min-width: 120px;
    text-align: center;
}

#gesture-toggle-btn.off {
    color: rgba(255, 100, 100, 0.7);
    border-color: rgba(255, 50, 50, 0.3);
}

#gesture-toggle-btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0, 255, 255, 0.2), transparent);
    transition: left 0.5s ease;
}

#gesture-toggle-btn:hover::before {
    left: 100%;
}

#gesture-toggle-btn:hover {
    background: rgba(0, 255, 255, 0.1);
    box-shadow: 0 0 15px rgba(0, 255, 255, 0.3);
    text-shadow: 0 0 5px rgba(0, 255, 255, 0.8);
}
